ALTER TABLE `notify_placeholder_columns`
ADD COLUMN `BASE_URL_ID` INT NULL AFTER `UPDATE_USER`,
ADD INDEX `NOTIFY_PLACEHOLDER_COLUMNS_FK2_idx` (`BASE_URL_ID` ASC) VISIBLE;

ALTER TABLE `notify_placeholder_columns`
ADD CONSTRAINT `NOTIFY_PLACEHOLDER_COLUMNS_FK2`
