UPDATE notification_type
SET SUBJECT='Action required: COI Annual disclosure Submitted by {COI_DISCLOSURE#REPORTER_NAME} waiting for review at {COI_DISCL_REVIEW#REVIEW_LOCATION} with {COI_DISCL_REVIEW#REVIEWER_REVIEW_STATUS} status.', MESSAGE='<p><span style="color:hsl(0,0%,0%);">Hello Reviewer,</span></p><p>COI Administrator {COI_DISCLOSURE#ADMINISTRATOR_NAME} assigned you to review the COI Annual Disclosure submitted on <span style="font-family:Arial, Helvetica, sans-serif;">{COI_DISCLOSURE#CERTIFICATION_DATE}</span><span style="color:hsl(0,0%,0%);"> by {COI_DISCLOSURE#REPORTER_NAME}.</span></p><p><span style="color:hsl(0,0%,0%);">Department: </span><span style="font-family:Arial, Helvetica, sans-serif;">{COI_DISCLOSURE#DEPARTMENT_NUMBER} - {COI_DISCLOSURE#DEPARTMENT_NAME}</span></p><p><span style="color:hsl(0,0%,0%);">Disclosure Status: </span><span style="font-family:Arial, Helvetica, sans-serif;">{COI_DISCLOSURE#DISCLOSURE_STATUS}</span></p><p><span style="color:hsl(0,0%,0%);">Review Location: {COI_DISCL_REVIEW#REVIEW_LOCATION}</span></p><p><span style="color:hsl(0,0%,0%);">Review Status: {COI_DISCL_REVIEW#REVIEWER_REVIEW_STATUS}</span></p><p><span style="color:hsl(0,0%,0%);font-family:Arial, Helvetica, sans-serif;">Please follow</span><span style="color:rgb(23,43,77);font-family:Arial, Helvetica, sans-serif;"> </span><a href="{COI_DISCL_URL#DISCLOSURE_URL}"><span style="font-family:Arial, Helvetica, sans-serif;">this link</span></a><span style="color:rgb(23,43,77);font-family:Arial, Helvetica, sans-serif;"> </span><span style="color:hsl(0,0%,0%);font-family:Arial, Helvetica, sans-serif;">to review the application.</span></p><p><span style="color:hsl(0,0%,0%);">Note: This is a system-generated email. Please do not reply to this email.</span></p>'
WHERE NOTIFICATION_TYPE_ID=8006;

