UPDATE `notification_type` SET `SUBJECT` = 'Action required: COI Disclosure Required for your {PROJECT_DETAIL#PROJECT_MODULE_ITEM_KEY} - {PROJECT_DETAIL#PROJECT_TITLE}', `MESSAGE` = '<p>Dear {PROJECT_DETAIL#PROJECT_PERSON_NAME},</p><p>You are required to complete a COI disclosure.</p><p><strong>Project: {PROJECT_DETAIL#PROJECT_TYPE}: {PROJECT_DETAIL#PROJECT_MODULE_ITEM_KEY} - {PROJECT_DETAIL#PROJECT_TITLE}</strong></p><p><strong>Department: {PROJECT_DETAIL#DEPARTMENT_NUMBER} - {PROJECT_DETAIL#DEPARTMENT_NAME}</strong></p><p>Please log in to the COI system and submit the necessary disclosure at your earliest convenience.</p><span style=\"color:hsl(0,0%,0%);font-family:Arial, Helvetica, sans-serif;\">Please find the list of awards by clicking </span><span style=\"color:rgb(23,43,77);font-family:Arial, Helvetica, sans-serif;\"> </span><a href=\"{DASHBOARD_URL#MY_AWARDS_DASHBOARD_URL}\"><span style=\"font-family:Arial, Helvetica, sans-serif;\">this link</span></a><span style=\"color:rgb(23,43,77);font-family:Arial, Helvetica, sans-serif;\"> </span></p><p>Note: This is a system-generated email. Please do not reply to this email.</p>' WHERE (`NOTIFICATION_TYPE_ID` = '8015');
