ALTER TABLE COI_DISCL_PROJECT_ENTITY_REL ADD COLUMN PROJECT_ENGAGEMENT_DETAILS VARCHAR(2000) NULL AFTER PROJECT_CONFLICT_STATUS_CODE;
-- Migration Script
UPDATE COI_DISCL_PROJECT_ENTITY_REL T1 SET T1.PROJECT_ENGAGEMENT_DETAILS = ( SELECT T4.COMMENT FROM DISCL_COMMENT T4 WHERE T4.MODULE_CODE = 8 AND T4.COMPONENT_TYPE_CODE = '1' AND T4.MODULE_ITEM_KEY = ( SELECT DISCLOSURE_ID FROM COI_DISCL_PROJECTS T2 WHERE T2.COI_DISCL_PROJECTS_ID = T1.COI_DISCL_PROJECTS_ID ) AND T4.SUB_MODULE_ITEM_KEY = T1.COI_DISCL_PROJECT_ENTITY_REL_ID ) WHERE EXISTS ( SELECT 1 FROM DISCL_COMMENT T4 WHERE T4.MODULE_CODE = 8 AND T4.COMPONENT_TYPE_CODE = '1' AND T4.MODULE_ITEM_KEY = ( SELECT DISCLOSURE_ID FROM COI_DISCL_PROJECTS T2 WHERE T2.COI_DISCL_PROJECTS_ID = T1.COI_DISCL_PROJECTS_ID ) AND T4.SUB_MODULE_ITEM_KEY = T1.COI_DISCL_PROJECT_ENTITY_REL_ID ); 
