UPDATE `NOTIFICATION_TYPE` SET `MESSAGE` = '<p style=\"margin-left:0px;\"><strong>Dear </strong>{OPA_DETAILS#REPORTER_NAME},</p><p style=\"margin-left:0px;\"><span style=\"background-color:rgb(255,255,255);color:rgb(64,64,64);\">Your Master OPA disclosure has reached its expiration date. Please review and submit your revised disclosure if needed. Below are the details of the expired or revised disclosure:</span></p><p style=\"margin-left:0px;\"><strong>Department: </strong>{OPA_DETAILS#DEPARTMENT_NUMBER} - {OPA_DETAILS#DEPARTMENT_NAME}</p><p style=\"margin-left:0px;\"><strong>Certification Date:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">{OPA_DETAILS#CERTIFICATION_DATE}</span></p><p style=\"margin-left:0px;\"><strong>Expiration Date:</strong> <span style=\"background-color:rgb(255,255,255);color:rgb(0,0,0);\">{OPA_DETAILS#EXPIRATION_DATE}</span></p><p style=\"margin-left:0px;\"><strong>Disposition Status</strong>: {OPA_DETAILS#DISPOSITION_STATUS}</p><p style=\"margin-left:0px;\">Please follow <a href=\"{OPA_URL#OPA_URL}\">this link</a> to review the application.</p><p>Note: This is a system-generated email. Please do not reply to this email.</p>' WHERE (`NOTIFICATION_TYPE_ID` = 8078);
