INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I don’t have any engagements to report. Should I submit an OPA disclosure?', 'Yes. If you are faculty, a researcher, research staff, academic instructional staff, postdocs, or otherwise directed, you should complete your OPA disclosure , even if you do not have any reasonably related engagements. Refer to MyCOI-OPA+ Resources for additional guidance.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I need to submit my disclosure but haven’t received information from my department. Can I access the new My COI-OPA+ system now to disclose?', 'Yes, the new system is available to faculty and academic and research staff to disclose both financial conflicts of interest and outside professional activities.\n\nIf you have Significant Financial Interests (SFIs) from the legacy MyCOI system, see MyCOI-OPA+ Resources for migration information.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I am an administrator and had to submit an OPA disclosure in prior years. Do I need to submit an OPA disclosure in MyCOI-OPA+?', 'No. Administrative, support, and service staff now report Outside Professional Activities directly to supervisors only as required by the Conflict of Commitment policy. They do not use MyCOI-OPA+.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'Is Travel Disclosure the same as the Travel Registry?', 'No, these are two separate functions.\n\nTravel Disclosure is required by some research agencies, requiring Sponsored Travel be included in the determination of whether the aggregate value of an Investigator’s financial interests rises to the level of a Significant Financial Interest. The Travel Registry allows the Institute to better locate and contact an MIT employee or student if traveling to an area where health, safety or security are threatened.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'Why can’t I see my OPA disclosures from prior to 2025?', 'MyCOI-OPA+ is a new system; historical data was not migrated.\nYou may access your last submitted OPA disclosure by going to the legacy OPA system.In the left hand Navigation menu, select “View My Current Submitted OPA Report”. This refers to your OPA report submitted in spring 2024.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'When do I need to submit my OPA disclosure?', 'During the initial deployment of MyCOI-OPA+, departments will send information regarding the submission of OPA disclosures. After the initial deployment, submit your OPA disclosure: 1) Within 30 days of starting a new engagement or renewing an existing one. 2) If 12 months have passed since your last OPA disclosure', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I am supposed to disclose my OPA, but I don’t have access to MyCOI-OPA+.', 'Contact ra-help@mit.edu', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I need to submit a Travel Disclosure for sponsored research– is that also done in My COI-OPA+?', 'Yes. Travel disclosures are entered in MyCOI-OPA+.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'How do graduate students report outside professional activities?', 'Graduate students considering outside professional activities should continue to follow the policies outlined in Graduate Policies and Procedures on COI and OPA. They will not use MyCOI-OPA+ unless specifically instructed to do so as part of a research proposal or award process.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'I\'m receiving a “Bad Request” error message in MyCOI-OPA+ - how do I resolve?', 'Try clearing cookies from your browser and using either Firefox or Chrome.  Safari and Brave browsers are generally not recommended and may contribute to these error messages.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'How do I disclose an FCOI and an OPA in MyCOI-OPA+?', 'Refer to MyCOI-OPA+ Resources for detailed guidance.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'Who reviews and approves my OPA disclosure?', 'Either your DAF or department head in your unit will approve your OPA.', now(), 'admin', '14');
INSERT IGNORE INTO FAQ (QUESTION_ID, QUESTION, ANSWER, UPDATE_TIMESTAMP, UPDATE_USER, CATEGORY_CODE) VALUES ((SELECT A.ID FROM (SELECT MAX(QUESTION_ID) + 1 AS ID FROM FAQ ) AS A), 'What is the difference between Institutional Time and Permitted Time?  Are these the same thing?', 'Institutional Time is the total time required for individuals, such as faculty and researchers, to fulfill their Institutional Responsibilities. Institutional Time includes Permitted Time for faculty and certain ranks.  Permitted Time is a specific portion of Institutional Time allocated for Outside Professional Activities (OPAs), allowing faculty to engage in activities beyond their core MIT responsibilities. MIT does not require reporting of specific hours or days spent on OPAs, relying on faculty to manage their time effectively to ensure that these activities do not compete with their institutional obligations. For faculty, Permitted Time is up to 20% of Institutional Time, for other defined ranks, it is up to 10%. All others do not have Permitted Time to engage in OPAs.', now(), 'admin', '14');

